let newLabel=document.querySelectorAll("section");const ariaLabelHeaders=()=>{let e=document.getElementsByTagName("article");for(let e=0;e<newLabel.length;e++)e<3&&newLabel[e].setAttribute("role","list");for(let t=0;t<e.length;t++)e[t].setAttribute("role","listitem")},ariaLabelButtons=()=>{let e=document.querySelectorAll("button");for(let t=0;t<e.length;t++){let l=e[t].querySelector("strong").innerText,r=newLabel[t].id;console.log("button: ",l),e[t].setAttribute("aria-label",r),e[t].setAttribute("aria-describedby",l)}};ariaLabelHeaders(),ariaLabelButtons();